#include &lt;avr/io.h&gt; /* Tells compiler the cpu speed to use for delays need to define it before importing util/delay.h otherwise define it with -DF_CPU=16000000UL during compilation */ #ifndef F_CPU #define F_CPU 16000000UL #endif #include &lt;util/delay.h&gt; /* PB5 has value of 5 */ #define LED PB5 #define SLEEP_MS 5000 // led is connected to PB5 int main(void) { /* initializing PB5 which is connected to port 13 of uno as output*/ DDRB |= (1&lt;&lt;LED); while (1) { /* toggling the PB5 to alternate between on-off state*/ PORTB ^= (1&lt;&lt;LED); _delay_ms(SLEEP_MS); } } Input using push button without any input pullup (debounce not implemented )
//atmega328p + switch test without debounce //pd7 is the input switch //pb5 is the led #include &lt;avr/io.h&gt; #include &lt;util/delay.h&gt; #ifndef F_CPU #define F_CPU 16000000UL #endif #define LED PB5 #define SW PD7 int main(void){ DDRB |= (1&lt;&lt;LED); //output //pb5 set 1 output DDRD &amp;= ~(1&lt;&lt;SW); //set 7th bit as input while(1){//main infinite loop if((PIND &amp; (1&lt;&lt;SW))) { PORTB ^= (1&lt;&lt;LED); //toggle led _delay_ms(3000);// delay for doing blink on pushed } else PORTB &amp;= ~(1&lt;&lt;LED); } return 0; } input pullup fo switch write 1 to the pin bit using PORTx=(1&laquo;PIN)
//atmega328p + switch test without debounce //pd7 is the input switch //pb5 is the led #include &lt;avr/io.h&gt; #include &lt;util/delay.h&gt; #ifndef F_CPU #define F_CPU 16000000UL #endif #define LED PB5 #define SW PD7 int main(void){ DDRB |= (1&lt;&lt;LED); //output //pb5 set 1 output DDRD &amp;= ~(1&lt;&lt;SW); //set 7th bit as input PORTD |=(1&lt;&lt;SW); //turning on internal pullup while(1){//main infinite loop if((PIND &amp; (1&lt;&lt;SW))==0)// button pressed { //blink led PORTB ^= (1&lt;&lt;LED); _delay_ms(3000); } else PORTB &amp;= ~(1&lt;&lt;LED); } return 0; }
